package ConditionalStatements;

public class SwitchStatement {

	
	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		String hghghg ="kar";
		switch (hghghg) 
		{
		case "hero":
		    System.out.println("this is bajaj");
			
			break;
			
		case "kar":
		    System.out.println("this is kar");
			
			break;
			
		case "bajaj":
		    System.out.println("this is baja ");
			
			break;

		default:
			System.out.println("this is worng");
			break;
		}
		
		
	}

}
